//imports
import { useState } from 'react';
import './PropertiesArea.scss';

//exports
export default function PropertiesArea(props) {
    //properties
    const customClass = props.componentData.customClass; //string
    const propertyGroup = props.componentData.propertyGroup; //obj
    const updatePropertyGroup = props.componentData.updatePropertyGroup; //any

    //states
    var [properties, setProperties] = useState(propertyGroup);

    //functions
    // const generatePropertiesArea = () => {
    //     if (dataType === "select") {
    //         return <SelectDropDown componentData={ { dropDownOptions : values, selectedOption : currentValue } } />

    //     } else if (dataType === "text") {
    //         return <TextInput componentData={ {  currentValue : currentValue } } />

    //     } else if (dataType === "number") {
    //         return (
    //             <NumberInput componentData={
    //                 {
    //                     currentIsRange : currentIsRange,
    //                     currentValue : currentValue,
    //                     currentMinValue : currentMinValue,
    //                     currentMaxValue : currentMaxValue,
    //                     currentDeviation : currentDeviation,
    //                     currentStandardFormIndex : currentStandardFormIndex
    //                 }
    //             } />
    //         )
    //     }
    // };

    //variables
    const componentClass = customClass ? "PropertiesArea " + customClass : "PropertiesArea"; //string

    if (properties.value) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <h4>{ properties.name }</h4>
                    <p>{ properties.description }</p>

                    <div className='propertyInputs'>
                        <div className='inner'>
                            {
                                properties.properties.length > 0 ?
                                    properties.properties.map((property, key) => {
                                        return (
                                            <></>
                                        );
                                    })
                                :
                                    <></>
                            }
                        </div>
                    </div>
    
                </div>
            </div>
        )
    } else {
        return <></>
    }
}
