/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './NumberInput.scss';

//interface
// const iNumberInput = { //interface?

// };

//exports
export default function NumberInput(props) {
    //properties

    //states
    var [numberInputValue, setNumberInputValue] = useState(""); //number

    //refs
    const numberInputValueRef = useRef(); //any

    //functions
    const handleNumberInputOnChange = (event) => {
        const targetElement = event.currenttarget ? event.currentTarget : event.target; //any

        if (targetElement) {
            const elmtValue = targetElement.value; //number

            setNumberInputValue(elmtValue);

        } else {
            //do nothing
        }
    };

    //variables
    const componentClass = "NumberInput"; //string

	return (
		<div className={ componentClass }>
            <div className="inner">
                <input ref={ numberInputValueRef } type="hidden" value={ numberInputValue ? numberInputValue : "" } />
                
                <input
                    type="number"
                    placeholder="Enter Number"
                    value={ numberInputValue ? numberInputValue : "" }
                    onChange={ handleNumberInputOnChange }
                />
            </div>
		</div>
	);
}
