/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef } from "react";
import PropertyInfoComponent from "../PropertyInfoComponent/PropertyInfoComponent";
import SelectDropDown from "../SelectDropDown/SelectDropDown";
import './PropertyScaleInput.scss';

//interface
// const iPropertyScaleInput = { //interface
//     scales : [ //Array<obj>
//         {
//             ID : 0, //number
//             name : "", //string
//             value : "", //string
//             reference : "", //string,
//             information : "", //string
//         }
//     ],
// };

//exports
export default function PropertyScaleInput(props) {
    //properties
    const scales = props.componentData.scales; //Array<obj>

    //refs
    const currentIDRef = useRef(); //any
    const currentValueRef = useRef(); //any
    const currentNameRef = useRef(); //any

    //functions
    const generateScaleInput = () => {
        if (scales.length === 1) {
            const unitID = scales[0].ID; //number
            const unitName = scales[0].name; //string
            const unitValue = scales[0].value; //string
            const unitReference = scales[0].reference; //string
            const unitInformation = scales[0].information; //string

            return (
                <>
                    <input ref={ currentIDRef } className="currentID" type="hidden" value={ !isNaN(unitID) ? unitID : "" } />
                    <input ref={ currentValueRef } className="currentValue" type="hidden" value={ unitValue ? unitValue : "" } />
                    <input ref={ currentNameRef } className="currentName" type="hidden" value={ unitName ? unitName : "" } />

                    <p>{ unitName ? unitName : "Unit Name not found" }</p>

                    <PropertyInfoComponent componentData={ { reference : unitReference, information : unitInformation } } />
                </>
            )

        } else if (scales.length > 1) {
            return (
                <SelectDropDown componentData={ { dropDownOptions : scales } } />
            )

        } else {
            return <></>
        }
    };

    //variables
    const componentClass = "PropertyScaleInput"; //string

    if (scales.length > 0) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <p>Scale</p>

                    <div className="inputContainer">
                        <SelectDropDown componentData={ { dropDownOptions : scales } } />
                    </div>
                </div>
            </div>
        )

    } else {
        return <></>
    }
}
