/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './NumberInput.scss';

//interface
// const iNumberInput = { //interface?
    // customClass : "", //string
    // inputPlaceholder : "", //string
    // initialValue : "", //string
    // initialName : "", //string

// };

//exports
export default function NumberInput(props) {
    //properties
    const customClass = props.componentData.customClass; //string
    const inputPlaceholder = props.componentData.inputPlaceholder; //string
    const initialValue = props.componentData.initialValue; //number
    const initialStandardFormIndex = props.componentData.initialStandardFormIndex; //number
    const initialName = props.componentData.initialName; //string

    //states
    var [numberInputValue, setNumberInputValue] = useState(initialValue);

    //refs
    const numberInputValueRef = useRef(); //any

    //functions
    const handleNumberInputOnChange = (event) => {
        const targetElement = event.currenttarget ? event.currentTarget : event.target;

        if (targetElement) {
            const elmtValue = targetElement.value;

            setNumberInputValue(elmtValue);

        } else {
            //do nothing
        }
    };

    //variables
    const componentClass = "NumberInput"; //string
    const componentInnerClass = customClass ? "inner " + customClass : "inner"; //string

	return (
		<div className={ componentClass }>
            <div className={ componentInnerClass }>
                <input ref={ numberInputValueRef } type="hidden" value={ numberInputValue ? numberInputValue : "" } />

                <label>{ initialName ? initialName : "No LABEL" }</label>
                <input
                    type="number"
                    placeholder={ inputPlaceholder }
                    value={ numberInputValue ? numberInputValue : "" }
                    onChange={ handleNumberInputOnChange }
                />
            </div>
		</div>
	);
}
