//imports
import SelectDropDown from "../SelectDropDown/SelectDropDown";
import './PropertyScaleInput.scss';

//interface
// const iPropertyScaleInput = { //interface
//     scales : [ //Array<obj>
//         {
//             ID : 0, //number
//             name : "", //string
//             value : "", //string
//             reference : "", //string,
//             information : "", //string
//         }
//     ],
// };

//exports
export default function PropertyScaleInput(props) {
    //properties
    const scales = props.componentData.scales; //Array<obj>

    //variables
    const componentClass = "PropertyScaleInput"; //string

    if (scales.length > 0) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <p>Scale</p>

                    <div className="inputContainer">
                        <SelectDropDown componentData={ { dropDownOptions : scales } } />
                    </div>
                </div>
            </div>
        )

    } else {
        return <></>
    }
}
