/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef } from "react";
import PropertyInfoComponent from "../PropertyInfoComponent/PropertyInfoComponent";
import SelectDropDown from "../SelectDropDown/SelectDropDown";
import './PropertyValueInput.scss';

//interface
// const iPropertyValueInput = { //interface
//     dataName : "", //sting
//     dataType : "", //string
//     values : [ //Array<obj>
//         {
//             ID : 0, //number
//             name : "", //string
//             value : "", //string
//             reference : "", //string,
//             information : "", //string
//         }
//     ],
// };

//exports
export default function PropertyValueInput(props) {
    //properties
    const dataName = props.componentData.dataName; //sting
    const dataType = props.componentData.dataType; //sting
    const values = props.componentData.values; //Array<obj>

    //refs
    const currentIDRef = useRef(); //any
    const currentValueRef = useRef(); //any
    const currentNameRef = useRef(); //any

    //functions
    const generateValueInput = () => {
        if (values.length === 1) {
            const valueID = values[0].ID; //number
            const valueName = values[0].name; //string
            const valueValue = values[0].value; //any
            const valueReference = values[0].reference; //string
            const valueInformation = values[0].information; //string

            return (
                <>
                    <input ref={ currentIDRef } className="currentID" type="hidden" value={ !isNaN(valueID) ? valueID : "" } />
                    <input ref={ currentValueRef } className="currentValue" type="hidden" value={ valueValue ? valueValue : "" } />
                    <input ref={ currentNameRef } className="currentName" type="hidden" value={ valueName ? valueName : "" } />

                    <p>{ valueName ? valueName : "Value Name not found" }</p>

                    <PropertyInfoComponent componentData={ { reference : valueReference, information : valueInformation } } />
                </>
            )

        } else if (values.length > 1) {
            return (
                <SelectDropDown componentData={ { dropDownOptions : values } } />
            )

        } else {
            return <></>
        }
    };

    //variables
    const componentClass = "PropertyValueInput"; //string

    if (values.length > 0) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <p>{ dataName ? dataName : "Value" }</p>

                    <div className="inputContainer">
                        { generateValueInput() }
                    </div>
                </div>
            </div>
        )

    } else {
        return <></>

    }
}
