//imports
import { useRef } from "react";
import PropertyInfoComponent from "../PropertyInfoComponent/PropertyInfoComponent";
import SelectDropDown from "../SelectDropDown/SelectDropDown";
import './PropertyUnitInput.scss';

//interface
// const iPropertyUnitInput = { //interface
//     units : [ //Array<obj>
//         {
//             ID : 0, //number
//             name : "", //string
//             value : "", //string
//             reference : "", //string,
//             information : "", //string
//         }
//     ],
// };

//exports
export default function PropertyUnitInput(props) {
    //properties
    const units = props.componentData.units; //Array<obj>

    //refs
    const currentIDRef = useRef(); //any
    const currentValueRef = useRef(); //any
    const currentNameRef = useRef(); //any

    //functions

    //variables
    const componentClass = "PropertyUnitInput"; //string

    if (units.length > 0) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <p>Unit</p>

                    <div className="inputContainer">
                        <SelectDropDown componentData={ { dropDownOptions : units } } />
                    </div>
                </div>
            </div>
        )

    } else {
        return <></>

    }
}
