/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './SelectDropDown.scss';

//interface
// const iSelectDropDown = { //interface?
    // dropDownOptions : [], //Array<obj>

// };

//exports
export default function SelectDropDown(props) {
    //properties
    const dropDownOptions = props.componentData.dropDownOptions; //Array<obj>

    //states
    var [selectDropDownIsOpen, setSelectDropDownIsOpen] = useState(false); //bool
    var [selectDropDownBodyHeight, setSelectDropDownBodyHeight] = useState(0); //number
    // var [selectDropDownValue, setSelectDropDownValue] = useState("");
    // var [selectDropDownName, setSelectDropDownName] = useState("");
    
    var [selectDropDownID, setSelectDropDownID] = useState("");
    var [selectDropDownValue, setSelectDropDownValue] = useState("");
    var [selectDropDownName, setSelectDropDownName] = useState("");

    //refs
    const selectDropDownBodyInnerRef = useRef(); //any
    const currentIDRef = useRef(); //any
    const currentValueRef = useRef(); //any
    const currentNameRef = useRef(); //any

    //functions
    const handleOpenCloseSelectDropDown = () => {
        const new_SelectDropDownIsOpen = !selectDropDownIsOpen; //bool
        const new_SelectDropDownBodyHeight = selectDropDownIsOpen ? 0 : selectDropDownBodyInnerRef.current.clientHeight; //number

        setSelectDropDownIsOpen(new_SelectDropDownIsOpen);
        setSelectDropDownBodyHeight(new_SelectDropDownBodyHeight)
    };
    const updateSelectedValueAndName = (event) => {
        const targetElement = event.currentTarget ? event.currentTarget : event.target;

        if (targetElement) {
            const elmtName = targetElement.innerText;
            const elmtValue = targetElement.getAttribute("optionvalue");
            //const elmtID = targetElement.getAttribute("optionid");

            setSelectDropDownValue(elmtValue);
            setSelectDropDownName(elmtName);
            openCloseSelectDropDown();

        } else {
            //do nothing
        }
    };
    const hideShowInformationPopUp = (event) => {
        event.stopPropagation();
        event.preventDefault();

        const targetElement = event.currentTarget ? event.currentTarget : event.target;

        if (targetElement) {
            const elmtIsActive = targetElement.classList.contains("active") ? true : false;
            const elmtParent = targetElement.parentElement;
            const nextSiblingElement = elmtParent.nextElementSibling;
            const elmtZIndex = elmtIsActive ? 1 : 3;
            const siblingZIndex = elmtIsActive ? 0 : 2;
            const siblingDisplay = elmtIsActive ? "none" : "grid";

            if (nextSiblingElement.classList.contains("informationPopUp")) {
                targetElement.style.zIndex = elmtZIndex;
                nextSiblingElement.style.zIndex = siblingZIndex;
                nextSiblingElement.style.display = siblingDisplay;

            }

            if (elmtIsActive) {
                targetElement.classList.remove("active");

            } else {
                targetElement.classList.add("active");

            }

        }
    };

    //variables
    const componentClass = selectDropDownIsOpen ? "SelectDropDown active" : "SelectDropDown"; //string
    const selectDropDownBodyStyle = { //any
        height : selectDropDownBodyHeight + "px"
    };

	return (
		<div className={ componentClass }>
            <div className="inner">
                <input ref={ currentIDRef } className="currentID" type="hidden" value={ !isNaN(selectDropDownID) ? selectDropDownID : "" } />
                <input ref={ currentValueRef } className="currentValue" type="hidden" value={ selectDropDownValue ? selectDropDownValue : "" } />
                <input ref={ currentNameRef } className="currentName" type="hidden" value={ selectDropDownName ? selectDropDownName : "" } />

                <div className="head">
                    <div className="inner" onClick={ handleOpenCloseSelectDropDown } >
                        <p>{ selectDropDownName ? selectDropDownName : "Please select an option" }</p>

                        <div className="openCloseButton">
                            <div className="inner">
                                <span />
                            </div>
                        </div>
                    </div>
                </div>

                <div className="body" style={ selectDropDownBodyStyle }>
                    <div className="inner" ref={ selectDropDownBodyInnerRef } >
                        {
                            dropDownOptions.length > 0 ?
                                dropDownOptions.map((option, key) => {
                                    return (
                                        <div key={ key } >
                                            <p
                                                onClick={ updateSelectedValueAndName }
                                                optionvalue={ option.value }
                                                optionid={ option.ID }
                                            >
                                                { option.name }

                                                

                                                {
                                                    option.information || option.reference ?
                                                        <span className="informationIcon" onClick={ hideShowInformationPopUp } ></span>
                                                    :
                                                        <></>
                                                }
                                            </p>

                                            {
                                                option.information || option.reference ?
                                                    <div className="informationPopUp">
                                                        {
                                                            option.information ?
                                                                <p>{ option.information }</p>
                                                            :
                                                                <></>
                                                        }

                                                        {
                                                            option.reference ?
                                                                <a href={ option.reference } rel="noreferrer" target="_blank">More info</a>
                                                            :
                                                                <></>
                                                        }   
                                                    </div>
                                                :
                                                    <></>
                                            }
                                        </div>
                                    )

                                }) 
                            :
                                <></> 
                        }
                    </div>
                </div>
            </div>
		</div>
	);
}
