/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './InfoComponent.scss';

//interface
// const iInfoComponent = { //interface
//     information : "" //string
//     reference : "" //string
// };

//exports
export default function InfoComponent(props) {
    //properties
    const information = props.componentData.information; //string
    const reference = props.componentData.reference; //string

    //states
    var [popUpIsActive, setPopUpIsActive] = useState(false);
    var [infoIconContent, setInfoIconContent] = useState("i");

    //refs
    const popUpRef = useRef();

    //functions
    const handlePopUpOpenClose = (event) => {
        const elmt = event.currentTarget ? event.currentTarget : event.target;
        const popUp = popUpRef.current;

        if (elmt && popUp) {
            if (popUpIsActive) {
                popUp.style.top = "unset";
                popUp.style.right = "unset";

                setInfoIconContent("i");

            } else {
                const elmtHeight = elmt.clientHeight;
                const bodyRectangle = document.body.getBoundingClientRect();
                const elemRectangle = elmt.getBoundingClientRect();
                const offsetTop = elemRectangle.top - bodyRectangle.top;
                const offsetRight = Math.abs(elemRectangle.right - bodyRectangle.right);
                
                popUp.style.top = (offsetTop + elmtHeight + 15) + "px";
                popUp.style.right = (offsetRight - 10) + "px";

                
                setInfoIconContent("x");
            }

            setPopUpIsActive(!popUpIsActive);
        }
    };

    //variables
    const popUpClass = popUpIsActive ? "infoPopUp active" : "infoPopUp";

	return (
		<>
            {
                information || reference ?
                    <>
                        <div className="infoIcon" onClick={ handlePopUpOpenClose }>
                            <p>{ infoIconContent }</p>
                        </div>

                        <div ref={ popUpRef } className={ popUpClass }>
                            <div className="inner">
                                {
                                    information ?
                                        <p>{ information }</p>
                                    :
                                        <></>
                                }

                                {
                                    reference ?
                                        <p>
                                            <a href={ reference } rel="noreferrer" target="_blank">Documentiaion</a>
                                        </p>
                                    :
                                        <></>
                                }
                            </div>
                        </div>
                    </>
                :

                    <></>
            }
        </>
	);
}
