/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './TextInput.scss';

//interface
// const iTextInput = { //interface?
    // customClass : "", //string
    // inputPlaceholder : "", //string
    // initialValue : "", //string
    // initialName : "", //string

// };

//exports
export default function TextInput(props) {
    //properties
    const customClass = props.componentData.customClass; //string
    const inputPlaceholder = props.componentData.inputPlaceholder; //string
    const initialValue = props.componentData.initialValue; //string
    const initialName = props.componentData.initialName; //string

    //states
    var [TextInputValue, setTextInputValue] = useState(initialValue);

    //refs
    const TextInputValueRef = useRef(); //any

    //functions
    const handleTextInputOnChange = (event) => {
        const targetElement = event.currenttarget ? event.currentTarget : event.target;

        if (targetElement) {
            const elmtValue = targetElement.value;

            console.log(elmtValue);

        } else {
            //do nothing
        }
    };

    //variables
    const componentClass = "TextInput"; //string
    const componentInnerClass = customClass ? "inner " + customClass : "inner"; //string

	return (
		<div className={ componentClass }>
            <div className={ componentInnerClass }>
                <input ref={ TextInputValueRef } type="hidden" value={ TextInputValue ? TextInputValue : "" } />

                <label>{ initialName ? initialName : "No LABEL" }</label>
                <input type="text" placeholder={ inputPlaceholder } value={ TextInputValue ? TextInputValue : "" } onBlur={ handleTextInputOnChange } onChange={ handleTextInputOnChange } />
            </div>
		</div>
	);
}
