/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link, useSearchParams  } from "react-router-dom";
import axios from "axios";
import './MaterialSearch.scss';
import { useEffect, useState } from "react";
import Grid from "../../Components/Grid/Grid";

//exports
export default function MaterialSearch(props) {
	//properties

	//states
    var [searchParams, setSearchParams] = useSearchParams();
	var [materials, setMaterials] = useState([]); //Array<obj>

	//refs

	//functions

	//variables
    const materialSearchRef = searchParams.get("materialRef");

	//Effects
	//get material
	useEffect(() => {
		axios.get("http://localhost:5000/materials/")
			.then((res) => {
				setMaterials(res.data);

			})
	}, []);

	return (
		<div className="MaterialSearch"> 
            <div className="inner">
                <Grid componentData={ { customClass : "MaterialContribuitions", numberOfColumns : 4 } }>
                    {
                        materials.map((material, key_1) => {
                            if (materialSearchRef ===  material.materialRef) {
                                return <></>

                            } else {
                                return (
                                    <div key={ key_1 } className="item">
                                        <Link to={ "/materials/fork/?materialRef=" + material.materialRef + "&forkRef=" + materialSearchRef } >
                                            <p>{ material.materialName }</p>

                                            <p>{ material.materialRef }</p>

                                            <p>
                                                {
                                                    material.materialAuthors.map((author, key_2) => {
                                                        return (
                                                            <span key={ key_2 }>{ author }</span>
                                                        )
                                                    })
                                                }
                                            </p>
                                        </Link>
                                    </div>
                                )
                            }
                        })
                    }
                </Grid>
            </div>		
		</div>
	);
}
