/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link, useSearchParams, useNavigate } from "react-router-dom";
import axios from "axios";
import './MaterialFork.scss';
import { useEffect, useState } from "react";
import Grid from "../../Components/Grid/Grid";

//exports
export default function MaterialFork(props) {
	//properties

	//states
    var [searchParams, setSearchParams] = useSearchParams();
	var [materials, setMaterials] = useState([]); //Array<obj>
    var [materialName, setmaterialName] = useState(""); //sting
    var [materialRef, setMaterialRef] = useState(""); //string
	var [materialCreatorUserName, setMaterialCreatorUserName] = useState(""); //string
	var [materialPublished, setMaterialPublished] = useState(false); //bool
	var [materialAuthors, setMaterialAuthors] = useState([]) //Array<string>

	//refs

	//functions
    const navigate = useNavigate();
    const test = (event) => {
        const elmt = event.currentTarget || event.target;
        const elmtRef = elmt.getAttribute("materialref");

        axios.get("http://localhost:5000/materials/findByRef/" + elmtRef)
            .then((res) => {
                const resData = res.data[0];

                if (resData) {
                    var newAuthors = materialAuthors;

                    for (var a = 0; a < resData.materialAuthors.length; a++) {
                        if (newAuthors.indexOf(resData.materialAuthors[a]) === -1) {
                            newAuthors.push(resData.materialAuthors[a]);

                        }
                    }

                    setMaterialAuthors(newAuthors);

                    const navigationTo = "/edit/?materialRef=" + MaterialRef; //string
                    const data = { //obj
                        materialName : materialName,
                        materialRef : materialRef,
                        materialCreatorUserName : materialCreatorUserName,
                        materialPublished : materialPublished,
                        materialAuthors : materialAuthors
                    };

                    axios.post("http://localhost:5000/materials/update/" + MaterialRef, data)
                        .then((res) => { console.log("success"); });
                    
                    navigate(navigationTo);
                }
            });
    };

	//variables
    const MaterialRef = searchParams.get("materialRef");

	//Effects

	//get materials
	useEffect(() => {
		axios.get("http://localhost:5000/materials/")
			.then((res) => {
				setMaterials(res.data);

			})
	}, []);
    useEffect(() => {
        axios.get("http://localhost:5000/materials/findByRef/" + MaterialRef)
            .then((res) => {
                const resData = res.data[0];

                if(resData) {
                    setmaterialName(resData.materialName);
                    setMaterialRef(resData.materialRef);
                    setMaterialCreatorUserName(resData.materialCreatorUserName);
                    setMaterialAuthors(resData.materialAuthors);
                }
            });
	}, [MaterialRef]);

	return (
		<div className="MaterialFork"> 
            <div className="inner">
                <Grid componentData={ { customClass : "", numberOfColumns : 4 } }>
                    {
                        materials.map((material, key_1) => {
                            if (MaterialRef ===  material.materialRef) {
                                return <div key={ key_1 } className="item" style={ { display: "none" } }/>

                            } else {
                                return (
                                    <div key={ key_1 } className="item">
                                        <div className="inner" materialref={ material.materialRef } onClick={ test } />
                                        <Link to={ "/materials/edit/?materialRef=" + MaterialRef } >
                                            <h4>{ material.materialName }</h4>

                                            <p>{ material.materialRef }</p>

                                            <p>
                                                {
                                                    material.materialAuthors.map((author, key_2) => {
                                                        return (
                                                            <span key={ key_2 }>{ author }</span>
                                                        )
                                                    })
                                                }
                                            </p>
                                        </Link>
                                    </div>
                                )
                            }
                        })
                    }
                </Grid>
            </div>		
		</div>
	);
}
