/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useState } from 'react';
import './PropertiesArea.scss';

//exports
export default function PropertiesArea(props) {
    //properties
    const customClass = props.componentData.customClass; //string
    const propertyGroup = props.componentData.propertyGroup; //obj
    const updatePropertyGroup = props.componentData.updatePropertyGroup; //any

    //states
    var [properties, setProperties] = useState(propertyGroup);

    //functions
    const generateProperty = (property) => {
        if (property.value) {
            return (
                <>
                    <h6>{ property.name }</h6>

                    { property.description ? <p>{ property.description }</p> : <></> }
                    
                    { generateInput(property) }
                </>
            )

        } else {
            return <></>
        }
    };
    const generateInput = (property) => {
        if (property.type === "text") {
            if (property.items.length > 0) {
                property.items.map((item, key) => {
                    return (
                        <div key={ key } className='propertyInput'>
                            <div className='inner'>
                                <input
                                    type="text"
                                    placeholder="Enter Value"
                                    value={ "" }
                                    onChange={ () => {} }
                                />
                            </div>
                        </div>
                    )
                })

            } else {
                return (
                    <div className='propertyInput'>
                        <div className='inner'>
                            <input
                                type="text"
                                placeholder="Enter Value"
                                value={""}
                                onChange={ () => {} }
                            />
                        </div>
                    </div>
                )
            }

        } else if (property.type === "number") {

        } else if (property.type === "select") {

        } else {
            return <></>
        }
    };

    //variables
    const componentClass = customClass ? "PropertiesArea " + customClass : "PropertiesArea"; //string
console.log(properties);
    if (properties.value) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <h4>{ properties.name }</h4>
                    <p>{ properties.description }</p>

                    <div className='properties'>
                        <div className='inner'>
                            {
                                properties.properties.length > 0 ?
                                    properties.properties.map((property, key) => {
                                        return (
                                            <div key={ key } className="property">
                                                <div className='inner'>
                                                    { generateProperty(property) }
                                                </div>
                                            </div>
                                        )
                                    })
                                :
                                    <></>
                            }
                        </div>
                    </div>
    
                </div>
            </div>
        )
    } else {
        return <></>
    }
}
