/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link } from "react-router-dom";
import axios from "axios";
import './MaterialPortal.scss';
import { useEffect, useState } from "react";

//exports
export default function MaterialPortal(props) {
	//properties
	const userID = props.pageData.userID;
	const userUserName = props.pageData.userName;
	const userFirstName = props.pageData.userFirstName;
	const userLastName = props.pageData.userLastName;

	//states
	var [materiralContributions, setMaterialContributions] = useState({ materials : [] });
	var [carouselTranslateX, setCarouselTranslateX] = useState(0);
	var [carouselTouchValue, setCarouselTouchValue] = useState(-1);

	
	const numCols = 4; //number
	const colRatio = ( 100 / numCols ); //number
	const numItems = materiralContributions.materials.length; //number

	//functions
	const shiftCarouselLeft = () => {
		const maxLeftValue = (-1) * colRatio * (numItems - numCols);
		const newLeftValue = carouselTranslateX - colRatio;

		if (newLeftValue >= maxLeftValue) {
			setCarouselTranslateX(newLeftValue);

		}
	};
	const shiftCarouselRight = () => {
		const maxRightValue = 0;
		const newRightValue = carouselTranslateX + colRatio;

		if (newRightValue <= maxRightValue) {
			setCarouselTranslateX(newRightValue);

		}
	};
	const handleCarouselTouchStart = (event) => {
		const touchValue = event.touches[0].clientX;
		console.log(touchValue)

		setCarouselTouchValue(touchValue);
	};
	const handleCarouselTouchEnd = (event) => {
		const touchValue = event.changedTouches[0].clientX;
		console.log(event)
		console.log(touchValue)

		if (touchValue > carouselTouchValue) {
			shiftCarouselLeft();

		} else if (touchValue < carouselTouchValue) {
			shiftCarouselRight();
		}

		setCarouselTouchValue(touchValue);
	};

	//variables
	var introContent = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum condimentum id libero vitae vehicula. Donec interdum nec purus eget condimentum. Nulla et porttitor turpis. Ut suscipit dolor eget sapien egestas mattis. Cras vehicula efficitur sem, id luctus arcu lobortis ut. Integer at laoreet massa. Quisque vitae nibh metus. Quisque pharetra porttitor arcu, eget mollis lectus dignissim id. Etiam eget nunc dignissim, scelerisque ligula et, sollicitudin nulla. Aliquam erat volutpat. Nulla efficitur blandit dui, nec auctor risus interdum a. Mauris aliquet ullamcorper massa, eget mattis mauris. Cras fringilla id risus quis fringilla. Vestibulum scelerisque blandit dolor non congue. Etiam et nisl vitae neque rutrum accumsan sit amet sed nisl.";

	useEffect(() => {
		axios.get("http://localhost:5000/materials/")
			.then((res) => {
				setMaterialContributions({ materials : res.data })
			})
	}, [])

	console.log(materiralContributions)
	return (
		<div className="MaterialPortal"> 
            <div className="inner">
                <div className='introContainer'>
					<div className='inner'>
						<h4>Welcome Back, { userFirstName }</h4>

						<p>{ introContent }</p>	
					</div>	
				</div> 

				<div className='MaterialContribuitionsContainer'>
					<div className='inner'>
						<h4>Manage Your Contributions</h4>

						<div className={ materiralContributions.materials.length > 4 ? 'contributions carousel' : 'contributions' }>
						{
							materiralContributions.materials.length > 4 ? 
								<div className="carouselButton">
									<div className="inner" onClick={ shiftCarouselLeft }/>
								</div>
							:
								<></>
						}
							<div className="outer" onTouchStart={ handleCarouselTouchStart } onTouchEnd={ handleCarouselTouchEnd } onTouchCancel={ handleCarouselTouchEnd } >
								<div
									className='inner'
									style={ { 
										gridTemplateColumns : "repeat(" + materiralContributions.materials.length + ", 1fr)",
										width : (materiralContributions.materials.length * 25) + "%",
										transform : "translateX(" + carouselTranslateX + "px)"
									}}
								>
									{
										materiralContributions.materials.map((material, key) => {
											return (
												<div key={ key } className="item">
													<a className="inner" href={ "http://localhost:3000/materials/?material_id=" + material._id }>
														<p>{ material.materialName }</p>
														<p>{ material.materialRef }</p>
														<p>{
															material.materialAuthors.map((author, key_2) => {
																return (
																	<span key={ key_2 }>{ author }</span>
																)
															})
														}</p>
													</a>
												</div>
											)
										})
									}
								</div>
							</div>

							{
								materiralContributions.materials.length > 4 ? 
									<div className="carouselButton">
										<div className="inner" onClick={ shiftCarouselRight }/>
									</div>
								:
									<></>
							}
						</div>
					</div>
				</div>

				<div className='MaterialCreationCotnainer'>
					<div className='inner'>
						<h4>Make New Contribution</h4>

						<p>Material Creation Here</p>

					</div>
				</div>
            </div>		
		</div>
	);
}
