/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link, useSearchParams  } from "react-router-dom";
import axios from "axios";
import './MaterialEdit.scss';
import { useEffect, useRef, useState } from "react";

//exports
export default function MaterialEdit(props) {
	//properties
	const userUserName = props.pageData.userUserName; //string

	//states
    var [searchParams, setSearchParams] = useSearchParams();
	var [materialName, setmaterialName] = useState(""); //sting
    var [materialRef, setMaterialRef] = useState(""); //string
	var [materialCreatorUserName, setMaterialCreatorUserName] = useState(""); //string
	var [materialAuthors, setMaterialAuthors] = useState([]) //Array<string>
	var [creatorFirstName, setCreatorFirstName] = useState(""); //string
	var [creatorLastName, setCreatorLastName] = useState(""); //string

	//refs

	//functions

	//variables
    const materialSearchRef = searchParams.get("materialRef");

	//Effects
	//set material data
	useEffect(() => {
		axios.get("http://localhost:5000/materials/findByRef/" + materialSearchRef)
            .then((res) => {
				const resData = res.data[0];

				setmaterialName(resData.materialName);
				setMaterialRef(resData.materialRef);
				setMaterialCreatorUserName(resData.materialCreatorUserName);
				setMaterialAuthors(resData.materialAuthors);
			});
	}, []);
	//set user data
	useEffect(() => {
		axios.get("http://localhost:5000/users/findByUserName/" + materialCreatorUserName)
			.then((res) => {
				const resData = res.data[0];

				setCreatorFirstName(resData.userFirstName);
				setCreatorLastName(resData.userLastName)

			});
	}, []);

	return (
		<div className="MaterialEdit"> 
            <div className="inner">
                
            </div>		
		</div>
	);
}
