	var [colorValue, setColorValue] = useState("");

	const colorInputRef = useRef();

	const handleColorInputOnChange = (event) => {
		const targetElement = event.currentTarget ? event.currentTarget : event.target;

		if (targetElement) {
			const elmtValue = targetElement.value;

			//validate value here

			setColorValue(elmtValue);
		}
	};

	return (
		<div className="App"> 
			<header />

			<main>
				<div className="propertyInputCotnainer">
					<div className="inner">
						<input ref={ colorInputRef } type="hidden" value={ colorValue } />

						<p>Color</p>

						<input type="text" value={ colorValue } onChange={ handleColorInputOnChange } />
					</div>
				</div>

			</main>

			<footer />
			{/* {
				(typeof backendData.items === "undefined") ?
					<p>Loading</p>
				:
					backendData.items.map((item, key) => {
						return (
							<p key={ key }>{ item }</p>
						)
					})
			} */}


		</div>
	);
}
