/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link } from "react-router-dom";
import axios from "axios";
import './MaterialPortal.scss';
import { useEffect, useRef, useState } from "react";
import Carousel from "../../Components/Carousel/Carousel";

//exports
export default function MaterialPortal(props) {
	//properties
	const userUserName = props.pageData.userUserName; //string

	//states
	var [userFirstName, setUserFirstName] = useState(null); //string
	var [materiralContributions, setMaterialContributions] = useState([]); //Array<obj>
	var [materialName, setMaterialName] = useState(""); //string
	var [materialRef, setMaterialRef] = useState(""); //string

	//refs
	const materialNameRef = useRef();

	//functions
	const handleMaterialNameOnChange = () => {

	}

	//variables
	var introContent = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum condimentum id libero vitae vehicula. Donec interdum nec purus eget condimentum. Nulla et porttitor turpis. Ut suscipit dolor eget sapien egestas mattis. Cras vehicula efficitur sem, id luctus arcu lobortis ut. Integer at laoreet massa. Quisque vitae nibh metus. Quisque pharetra porttitor arcu, eget mollis lectus dignissim id. Etiam eget nunc dignissim, scelerisque ligula et, sollicitudin nulla. Aliquam erat volutpat. Nulla efficitur blandit dui, nec auctor risus interdum a. Mauris aliquet ullamcorper massa, eget mattis mauris. Cras fringilla id risus quis fringilla. Vestibulum scelerisque blandit dolor non congue. Etiam et nisl vitae neque rutrum accumsan sit amet sed nisl."; //string

	//Effects
	useEffect(() => {
		axios.get("http://localhost:5000/materials/findByAuthor/" + userUserName)
			.then((res) => {
				setMaterialContributions(res.data);

			})
	}, []);

	useEffect(() => {
		axios.get("http://localhost:5000/users/findByUserName/" + userUserName)
			.then((res) => {
				const resData = res.data[0];

				setUserFirstName(resData.userFirstName);

			})
	}, []);

	useEffect(() => {
		const generateMaterialRef = () => {
			const referencePartValue = Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
		
			return referencePartValue;
		};

		setMaterialRef(generateMaterialRef);
	}, []);

	return (
		<div className="MaterialPortal"> 
            <div className="inner">
                <div className='introContainer'>
					<div className='inner'>
						<h4>Welcome Back, { userFirstName }</h4>

						<p>{ introContent }</p>	
					</div>	
				</div> 

				<div className='MaterialContribuitionsContainer'>
					<div className='inner'>
						<h4>Manage Your Contributions</h4>

						<Carousel componentData={ { customClass : "MaterialContribuitions", numberOfColumns : 4 } } >
							{
								materiralContributions.map((material, key_1) => {
									return (
										<div key={ key_1 } className="item">
											<Link to={ "/materials/?material_id=" + material._id } >
												<p>{ material.materialName }</p>

												<p>{ material.materialRef }</p>

												<p>
													{
														material.materialAuthors.map((author, key_2) => {
															return (
																<span key={ key_2 }>{ author }</span>
															)
														})
													}
												</p>
											</Link>
										</div>
									)
								})
							}
						</Carousel>
					</div>
				</div>

				<div className='MaterialCreationCotnainer'>
					<div className='inner'>
						<h4>Make New Contribution</h4>

						<div className="MaterialCreation">
							<div className="inner">
								<div className='infoContainer'>
									<div className='inner'>
										<h4>New recipe</h4>

										<p>To get started, give your recipe a name and choose how you'd to input data.</p>	
									</div>	
								</div> 

								<form>
									<div className='materialName'>
										<div className="inner">
											<h4 className="required">Recipe Title</h4>

											<input ref={ materialNameRef } type="text" value={ materialName } placeholder="Enter Recipe Title" onChange={ handleMaterialNameOnChange } />
										</div>
									</div>	

									<div className='materialRef'>
										<div className="inner">
											<h4 className="required">Recipe Title</h4>

											<p>{ materialRef }</p>
										</div>
									</div>	
								</form>
							</div>
						</div>

					</div>
				</div>
            </div>		
		</div>
	);
}
