/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useState } from 'react';
import './OptionInput.scss';

//exports
export default function OptionInput(props) {
    //properties
    const initialOptionName = props.componentData.initialOptionName; //string
    const initialOptionRef = props.componentData.initialOptionRef; //string
    const options = props.componentData.options; //Array<obj>

    //states
    var [selectedOptionName, setSelectedOptionName] = useState(initialOptionName);
    var [selectedOptionRef, setSelectedOptionRef] = useState(initialOptionRef);
    var [optionInputIsOpen, setOptionInputIsOpen] = useState(false);

    //refs

    //functions
    const handleOptionOnChange = () => {
        console.log("handle option on change");
    };

    //variables
    const componentClass = optionInputIsOpen ? "OptionInput active" : "OptionInput"; //string

	return (
		<div className={ componentClass }>
            <div className="inner">
                <div className="head">
                    <input type="hidden" value={ selectedOptionName } />
                    <input type="hidden" value={ selectedOptionRef } />
                    <p>{ selectedOptionName || "Select a tool" }</p>
                </div>

                <div className="body">
                    <div className="inner"> 
                        {
                            options.map((option, key) => {
                                const optionClass = selectedOptionRef === option.ref ? "option active" : "option";

                                return (
                                    <p
                                        key={ key }
                                        className={ optionClass }
                                        onClick={ handleOptionOnChange }
                                        optionname={ option.name }
                                        optionref={ option.ref }
                                    >
                                        { option.name }
                                    </p>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
	);
}
