/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './SelectDropDown.scss';

//interface
// const iSelectDropDown = { //interface?
    // customClass : "", //string
    // initialValue : "", //string
    // initialName : "", //string
    // dropDownOptions : [], //Array<obj>

// };

//exports
export default function SelectDropDown(props) {
    //properties
    const customClass = props.componentData.customClass; //string
    const initialValue = props.componentData.initialValue; //string
    const initialName = props.componentData.initialName; //string
    const dropDownOptions = props.componentData.dropDownOptions; //Array<obj>

    //states
    var [selectDropDownIsOpen, setSelectDropDownIsOpen] = useState(false); //bool
    var [selectDropDownBodyHeight, setSelectDropDownBodyHeight] = useState(0); //number
    var [selectDropDownValue, setSelectDropDownValue] = useState(initialValue);
    var [selectDropDownName, setSelectDropDownName] = useState(initialName);

    //refs
    const selectDropDownBodyInnerRef = useRef(); //any
    const selectDropDownInputValue = useRef(); //any
    const selectDropDownInputName = useRef(); //any

    //functions
    const openCloseSelectDropDown = () => {
        const new_SelectDropDownIsOpen = !selectDropDownIsOpen; //bool
        const new_SelectDropDownBodyHeight = selectDropDownIsOpen ? 0 : selectDropDownBodyInnerRef.current.clientHeight; //number

        setSelectDropDownIsOpen(new_SelectDropDownIsOpen);
        setSelectDropDownBodyHeight(new_SelectDropDownBodyHeight)
    };
    const updateSelectedValueAndName = (event) => {
        const targetElement = event.currentTarget ? event.currentTarget : event.target;

        if (targetElement) {
            const elmtName = targetElement.innerText;
            const elmtValue = targetElement.getAttribute("optionvalue");
            //const elmtID = targetElement.getAttribute("optionid");

            setSelectDropDownValue(elmtValue);
            setSelectDropDownName(elmtName);
            openCloseSelectDropDown();

        } else {
            //do nothing
        }
    };

    //variables
    const componentClass = selectDropDownIsOpen ? "SelectDropDown active" : "SelectDropDown"; //string
    const componentInnerClass = customClass ? "inner " + customClass : "inner"; //string
    const selectDropDownBodyStyle = { //any
        height : selectDropDownBodyHeight + "px"
    };

	return (
		<div className={ componentClass }>
            <div className={ componentInnerClass }>
                <input ref={ selectDropDownInputValue } type="hidden" value={ selectDropDownValue ? selectDropDownValue : "" } />                
                <input ref={ selectDropDownInputName } type="hidden" value={ selectDropDownName ? selectDropDownName : "" } />

                <div className="head">
                    <div className="inner" onClick={ openCloseSelectDropDown } >
                        <p>{ selectDropDownName ? selectDropDownName : "Please select an option" }</p>

                        <div className="openCloseButton">
                            <div className="inner">
                                <span />
                            </div>
                        </div>
                    </div>
                </div>

                <div className="body" style={ selectDropDownBodyStyle }>
                    <div className="inner" ref={ selectDropDownBodyInnerRef } >
                        {
                            dropDownOptions.length > 0 ?
                                dropDownOptions.map((option, key) => {
                                    return (
                                        <>
                                            <p
                                                key={ key }
                                                onClick={ updateSelectedValueAndName }
                                                optionvalue={ option.value }
                                                optionid={ option.ID }
                                            >
                                                { option.name }

                                                

                                                {
                                                    option.information || option.reference ?
                                                        <span className="informationIcon"></span>
                                                    :
                                                        <></>
                                                }
                                            </p>

                                            {
                                                option.information || option.reference ?
                                                    <div className="informationPopUp">
                                                        {
                                                            option.information ?
                                                                <p>{ option.information }</p>
                                                            :
                                                                <></>
                                                        }

                                                        {
                                                            option.reference ?
                                                                <a href={ option.reference } rel="noreferrer" target="_blank">More info</a>
                                                            :
                                                                <></>
                                                        }   
                                                    </div>
                                                :
                                                    <></>
                                            }
                                        </>
                                    )

                                }) 
                            :
                                <></> 
                        }
                    </div>
                </div>
            </div>
		</div>
	);
}
