/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

import { useEffect, useState } from "react";
import { useNavigate, useSearchParams  } from "react-router-dom";
import axios from "axios";
import './MaterialEdit.scss';
import Grid from "../../Components/Grid/Grid";
import Carousel from "../../Components/Carousel/Carousel";

//exports
export default function MaterialEdit(props) {
	//properties
	const userUserName = props.pageData.userUserName; //string

	//states
    var [searchParams, setSearchParams] = useSearchParams();

	var [materialName, setmaterialName] = useState(""); //sting
    var [materialRef, setMaterialRef] = useState(""); //string
	var [materialForkedFrom, setMaterialForkedFrom] = useState(""); //string
	var [materialCreatorUserName, setMaterialCreatorUserName] = useState(""); //string
	var [materialPublished, setMaterialPublished] = useState(false); //bool 
	var [materialCreationDate, setMaterialCreationDate] = useState(""); //string
	var [materialUpdetedDate, setMaterialUpdetedDate] = useState(""); //string
	var [creatorFirstName, setCreatorFirstName] = useState(""); //string
	var [creatorLastName, setCreatorLastName] = useState(""); //string

	var [materialAuthors, setMaterialAuthors] = useState([]) //Array<string>
	var [materialDescription, setMaterialDescription] = useState(""); //string
	var [materialCoverImageSrc, setMaterialCoverImageSrc] = useState(""); //string

	//functions
	const navigate = useNavigate();
	const handleSaveMaterial = () => {
		const postTo = "http://localhost:5000/materials/update/" + materialRef; //string
		const data = { //obj
			materialName : materialName,
			materialRef : materialRef,
			materialForkedFrom : materialForkedFrom,
			materialCreatorUserName : materialCreatorUserName,
			materialPublished : materialPublished,

			materialAuthors : materialAuthors,
			materialDescription : materialDescription,
            materialCoverImageSrc : materialCoverImageSrc,
		};

		axios.post(postTo, data)
			.then((res) => { console.log("Material Forked"); });
	};
	const handlePublishMaterial = () => {
		setMaterialPublished(true);
		const postTo = "http://localhost:5000/materials/update/" + materialRef; //string
		const data = { //obj
			materialPublished : true,
		};

		axios.post(postTo, data)
			.then((res) => { console.log("Material Forked"); });

		navigate("/");
	};

	//variables
    const MaterialRef = searchParams.get("materialRef");
	const materialEditClassName = materialPublished ? "MaterialEdit published" : "MaterialEdit";

	//Effects
		//set material data
		useEffect(() => {
			axios.get("http://localhost:5000/materials/findByRef/" + MaterialRef)
				.then((res) => {
					const resData = res.data[0];

					if (resData) {
						setmaterialName(resData.materialName);
						setMaterialRef(resData.materialRef);
						setMaterialForkedFrom(resData.materialForkedFrom);
						setMaterialCreatorUserName(resData.materialCreatorUserName);
						setMaterialPublished(resData.marterialPublished);
						setMaterialCreationDate(resData.createdAt);
						setMaterialUpdetedDate(resData.updatedAt);

						setMaterialAuthors(resData.materialAuthors);
						setMaterialDescription(resData.materialDescription);
						setMaterialCoverImageSrc(resData.materialCoverImageSrc);
					}
				});
		}, [MaterialRef]);

		//set user data
		useEffect(() => {
			if (materialCreatorUserName) {
				axios.get("http://localhost:5000/users/findByUserName/" + materialCreatorUserName)
					.then((res) => {
						const resData = res.data[0];

						if (resData) {
							setCreatorFirstName(resData.userFirstName);
							setCreatorLastName(resData.userLastName);
						}

					});
			}
		}, [materialCreatorUserName]);

	return (
		<div className={ materialEditClassName }> 
            <div className="inner">
				<div className="functionalButtonsContainer">
					<div className="inner">
						<div className="functionalButton">
							<p onClick={ handleSaveMaterial }>Save</p>
						</div>

						<div className="functionalButton">
							<p onClick={ handlePublishMaterial }>Publish</p>
						</div>
					</div>
				</div>

				<div className="coverImageContainer">

				</div>

                <div className="infoContainer">
					<div className="inner">
						<h4>{ materialName }</h4>

						<p>Created By: { creatorFirstName + " " + creatorLastName }</p>
						
						<p>Date Created: { materialCreationDate }</p>

						<p>Last Updated: { materialUpdetedDate }</p>

						<p>REF: { materialRef }</p>

						{
							materialForkedFrom ? 
								<p>Forked From : { materialForkedFrom }</p>
							:
								<></>
						}
						
					</div>
				</div>

				<div className="detailsCotnainer">
					<Grid componentData={ { customClass : "", numberOfColumns : 2} } >
						<div className="details">
							<div className="inner">

							</div>
						</div>

						<div className="details">
							<div className="inner">
								
							</div>
						</div>
					</Grid>
				</div>

				<div className="ingredientsContainer">

				</div>

				<div className="propertiesContainer">

				</div>

				<div className="methodContainer">

				</div>

				<div className="imageGalleryContainer">
					<Carousel componentData={ { customClass : "", numberOfColumns : 4 } }>

					</Carousel>
				</div>
            </div>		
		</div>
	);
}
