import PropertyInput from './Components/PropertyInput/PropertyInput';

//exports
export default function App() {
	const propertyDataGroups = [
        {
            ID : 0,
            name : "Physical Properties",
            allowMultiple : false,
            dataGroup : [
                {
                    ID : 0,
                    color : {
                        allowMultiple : false,
						duplicateFields : [],
