						items : []
                    },
                    odour : {
                        allowMultiple : false,
						duplicateFields : [],
						name : "odour",
						description : "this is a description",
						type : "text",
						values : [],
						units : [],
						scales : [],        
						items : []
                    },
				}
			]
		}
	];

	return (
		<div className="App"> 
			<header />

			<main>
				<PropertyInput componentData={
					{ 
						dataObject : {
							allowMultiple : false,
							duplicateFields : [],
							name : "color",
							description : "this is a description",
							type : "text",
							values : [],
							units : [],
							scales : [],        
							items : []
						}
					} 
				} />
			</main>

			<footer />			
		</div>
	);
}
