	Author(s) : David Charles - AddMustard
*/

//imports
//import { useRef, useState } from "react";
import InfoComponent from "../InfoIconWithPopUp/InfoComponent";
import PropertyScaleInput from "../PropertyScaleInput/PropertyScaleInput";
import PropertyUnitInput from "../PropertyUnitInput/PropertyUnitInput";
import PropertyValueInput from "../PropertyValueInput/PropertyValueInput";
import './PropertyInput.scss';

//interface
// const iPropertyInput = { //interface?
    //dataObject : {} //obj

// };

// const colorObject = {
//     allowMultiple : false,
//     duplicateFields : [],
//     name : "color",
//     description : "",
//     type : "text",
//     values : [],
//     units : [],
//     scales : [],        
//     items : [
//         {
//             ID : 0,
//             value : "green",
//             units : "",
//             scale : "",
//             deviation : "",
//             edits : [
//                 {
//                     ID : 0,
//                     date : "",
//                     editor : "",
//                 }
//             ],
//         }
//     ], 
// };

//exports
export default function PropertyInput(props) {
    //dataObject
    const dataObject = props.componentData.dataObject; //obj

    if (dataObject) {
        //properties
        //const allowMultiple = dataObject.allowMultiple; //bool
        //const duplicateFields = dataObject.duplicateFields; //Array<string>
        const dataName = dataObject.name; //string
        const description = dataObject.description; //string
        const dataType = dataObject.type; //string
        const values = dataObject.values; //Array<obj>
        const units = dataObject.units; //Array<obj>
        const scales = dataObject.scales; //Array<obj>
        //const items = dataObject.items; //Array<obj>

        //functions
        const generateBodyClass = () => {
            var bodyClass = "body";

            if (units.length > 0 && scales.length > 0) {
                bodyClass += " threeColumn";

            } else if (units.length > 0 || scales.length > 0) {
                bodyClass += " twoColumn";

            }

            return bodyClass;
        };

        //variables
        const componentClass = "PropertyInput"; //string
        
        return (
            <div className={ componentClass }>
                <div className="inner">
                    <div className="head">
                        <p>{ dataName ? dataName : "No Data Name" }</p>

                        <InfoComponent componentData={ { information : description, reference : null } } />
                    </div>
                    
                    <div className={ generateBodyClass() }>
                        <PropertyValueInput componentData={ { dataType : dataType, values : values } } />

                        <PropertyUnitInput componentData={ { units : units } } />

                        <PropertyScaleInput componentData={ { scales : scales } } />
                    </div>
                </div>
            </div>
        )

    } else {
        return (
            <p>Data Object Does Not exist</p>
        )
    }
}
