/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/
import { Link, useSearchParams  } from "react-router-dom";
import axios from "axios";
import './MaterialFork.scss';
import { useEffect, useRef, useState } from "react";

//exports
export default function MaterialFork() {
	//properties

	//states
    var [searchParams, setSearchParams] = useSearchParams();
	var [materialName, setmaterialName] = useState(""); //sting
    var [materialRef, setMaterialRef] = useState(""); //string
	var [materialCreatorUserName, setMaterialCreatorUserName] = useState(""); //string
	var [materialCreationDate, setMaterialCreationDate] = useState(""); //string
	var [materialUpdetedDate, setMaterialUpdetedDate] = useState(""); //string
	var [materialAuthors, setMaterialAuthors] = useState([]) //Array<string>
	var [creatorFirstName, setCreatorFirstName] = useState(""); //string
	var [creatorLastName, setCreatorLastName] = useState(""); //string

	//refs

	//functions

	//variables
    const materialSearchRef = searchParams.get("materialRef");
    const materialForkRef = searchParams.get("forkRef");

	//Effects
	//set fork data
	useEffect(() => {
		axios.get("http://localhost:5000/materials/findByRef/" + materialForkRef)
            .then((res) => {
				const resData = res.data[0];

				setMaterialAuthors(resData.materialAuthors);
			});
	}, []);
    //set current data
    useEffect(() => {
		axios.get("http://localhost:5000/materials/findByRef/" + materialSearchRef)
            .then((res) => {
				const resData = res.data[0];

				setmaterialName(resData.materialName);
				setMaterialRef(resData.materialRef);
				setMaterialCreatorUserName(resData.materialCreatorUserName);
				setMaterialCreationDate(resData.createdAt);
				setMaterialUpdetedDate(resData.updatedAt);
				setMaterialAuthors(materialAuthors.push(resData.materialCreatorUserName));
			});
	}, []);
	//set user data
	useEffect(() => {
		axios.get("http://localhost:5000/users/findByUserName/" + materialCreatorUserName)
			.then((res) => {
				const resData = res.data[0];

				setCreatorFirstName(resData.userFirstName);
				setCreatorLastName(resData.userLastName)

			});
	}, [materialCreatorUserName]);

	return (
		<div className="MaterialFork"> 
            <div className="inner">
                <div className="infoContainer">
					<div className="inner">
						<h4>{ materialName }</h4>

						<p>Created By: { creatorFirstName + " " + creatorLastName }</p>

						<p>REF: { materialRef }</p>
						
                        <p>{
                            materialAuthors.map((author, key) => {
                                return <span key={ key }>{ author }</span>
                            })
                        }</p>
					</div>
				</div>
            </div>		
		</div>
	);
}
