/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef } from "react";
import PropertyInfoComponent from "../PropertyInfoComponent/PropertyInfoComponent";
import SelectDropDown from "../SelectDropDown/SelectDropDown";
import './PropertyScalesInput.scss';

//interface
// const iPropertyScalesInput = { //interface
//     scales : [ //Array<obj>
//         {
//             ID : 0, //number
//             name : "", //string
//             value : "", //string
//             reference : "", //string,
//             information : "", //string
//         }
//     ],
// };

//exports
export default function PropertyScalesInput(props) {
    //properties
    const scales = props.componentData.scales; //Array<obj>

    //states

    //refs
    const currentIDRef = useRef();
    const currentValueRef = useRef();
    const currentNameRef = useRef();

    //functions
    const generateScalesInput = () => {
        if (scales.length === 1) {
            const unitID = scales[0].ID;
            const unitName = scales[0].name;
            const unitValue = scales[0].value;
            const unitReference = scales[0].reference;
            const unitInformation = scales[0].information;

            return (
                <>
                    <input ref={ currentIDRef } className="currentID" type="hidden" value={ !isNaN(unitID) ? unitID : "" } />
                    <input ref={ currentValueRef } className="currentValue" type="hidden" value={ unitValue ? unitValue : "" } />
                    <input ref={ currentNameRef } className="currentName" type="hidden" value={ unitName ? unitName : "" } />

                    <p>{ unitName ? unitName : "Unit Name not found" }</p>

                    <PropertyInfoComponent componentData={ { reference : unitReference, information : unitInformation } } />
                </>
            )

        } else if (scales.length > 1) {
            return (
                <SelectDropDown componentData={ { dropDownOptions : scales } } />
            )

        } else {
            return <></>
        }
    }

    //variables
    const componentClass = "PropertyScalesInput"; //string

    if (scales.length > 1) {
        return (
            <div className={ componentClass }>
                <div className="inner">
                    { generateScalesInput() }
                </div>
            </div>
        )

    } else {
        return <></>
    }
}
