/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

//imports
import { useRef, useState } from "react";
import './PropertyUnitsInput.scss';

//interface
const iPropertyUnitsInput = { //interface
    units : [ //Array<obj>
        {
            ID : 0, //number
            name : "", //string
            value : "", //string
            reference : "", //string,
            information : "", //string
        }
    ],
};

//exports
export default function PropertyUnitsInput(props) {
    //properties
    const units = props.componentData.units; //Array<obj>

    //states

    //refs
    const currentIDRef = useRef();
    const currentValueRef = useRef();
    const currentNameRef = useRef();

    //functions
    const generateUnitsInput = () => {
        if (units.length === 1) {
            const unitID = units[0].ID;
            const unitName = units[0].name;
            const unitValue = units[0].value;
            const unitReference = units[0].reference;
            const unitInformation = units[0].information;

            return (
                <>
                    <input ref={ currentIDRef } className="currentUnit" type="hidden" value={ !isNaN(unitID) ? unitID : "" } />
                    <input ref={ currentValueRef } className="currentUnit" type="hidden" value={ unitValue ? unitValue : "" } />
                    <input ref={ currentNameRef } className="currentUnit" type="hidden" value={ unitName ? unitName : "" } />

                    <p>{ unitName ? unitName : "Unit Name not found" }</p>

                    {
                        unitReference || unitInformation ?
                            <>
                                <div className="infoIcon">
                                    <div className="inner" />
                                </div>

                                <div className="infoPopUp">
                                    <div className="inner">
                                        {
                                            unitInformation ?
                                                <p>{ unitInformation }</p>
                                            :
                                                <></>
                                        }

                                        {
                                            unitReference ?
                                                <p>
                                                    <a href={ unitReference } rel="noreferrer" target="_blank">Documentiaion</a>
                                                </p>
                                            :
                                                <></>
                                        }
                                    </div>
                                </div>
                            </>
                        :
                            <></>
                    }
                </>
            )

        } else if (units.length > 1) {
            return (
                <p>Multiple</p>
            )

        } else {
            return <></>
        }
    }

    //variables
    const componentClass = "PropertyUnitsInput"; //string

	return (
		<div className={ componentClass }>
            <div className="inner">
                { generateUnitsInput() }
            </div>
		</div>
	);
}
