/*
	Project : Materiom
	Author(s) : David Charles - AddMustard
*/

import { useRef, useState } from 'react';
import './App.scss';

//exports
export default function App() {

	const colorObject = {
		allowMultiple : false,
		duplicateFields : [],
		name : "color",
		description : "",
		type : "text",
		values : [],
		units : [],
		scales : [],        
		items : [], 
	};

	var [colorValue, setColorValue] = useState(colorObject);
	var [colorUnit, setColorValue] = useState(colorObject);
	var [colorScale, setColorValue] = useState(colorObject);

	const colorValueRef = useRef();
	const colorUnitRef = useRef();
	const colorScaleRef = useRef();

	const handleColorInputOnChange = (event) => {
		const targetElement = event.currentTarget ? event.currentTarget : event.target;

		if (targetElement) {
			const elmtValue = targetElement.value;

			//validate value here

			setColorValue(elmtValue);
		}
	};

	return (
		<div className="App"> 
			<header />

			<main>
				<div className="propertyInputCotnainer">
					<div className="inner">

						{
							colorObject.values.length === 1 ?
								<></>
							:
								<></>
						}
					</div>
				</div>

			</main>

			<footer />			
		</div>
	);
}
